const axios = require('axios'); // Assuming axios is installed in your app.functions
const KICKBOX_API_KEY = process.env.kickbox;
const contactEmail = event.outputFields.contactEmail;
const kickboxResponse = await axios.get(`https://api.kickbox.com/v2/verify?email=${contactEmail}&apikey=${KICKBOX_API_KEY}`);
callback(kickboxResponse.data);
